lign="center" bgcolor="#f1f9ff">φ</td>          <td>&amp;phi;</td>          <td>&amp;#966;</td>          <td align="center" bgcolor="#f1f9ff">χ</td>          <td>&amp;chi;</td>          <td>&amp;#967;</td>          <td align="center" bgcolor="#f1f9ff">ψ</td>          <td>&amp;psi;</td>          <td>&amp;#968;</td>        </tr>        <tr>          <td align="center" bgcolor="#f1f9ff">ω</td>          <td>&amp;omega;</td>          <td>&amp;#969;</td>          <td align="center" bgcolor="#f1f9ff">ϑ</td>          <td>&amp;thetasym;</td>          <td>&amp;#977;</td>          <td align="center" bgcolor="#f1f9ff">ϒ</td>          <td>&amp;upsih;</td>          <td>&amp;#978;</td>        </tr>        <tr>          <td align="center" bgcolor="#f1f9ff">ϖ</td>          <td>&amp;piv;</td>          <td>&amp;#982;</td>          <td align="center" bgcolor="#f1f9ff">•</td>          <td>&amp;bull;</td>          <td>&amp;#8226;</td>          <td align="center" bgcolor="#f1f9ff">…</td>          <td>&amp;hellip;</td>          <td>&amp;#8230;</td>        </tr>        <tr>          <td align="center" bgcolor="#f1f9ff">′</td>          <td>&amp;prime;</td>          <td>&amp;#8242;</td>          <td align="center" bgcolor="#f1f9ff">″</td>          <td>&amp;Prime;</td>          <td>&amp;#8243;</td>          <td align="center" bgcolor="#f1f9ff">‾</td>          <td>&amp;oline;</td>          <td>&amp;#8254;</td>        </tr>        <tr>          <td align="center" bgcolor="#f1f9ff">⁄</td>          <td>&amp;frasl;</td>          <td>&amp;#8260;</td>          <td align="center" bgcolor="#f1f9ff">℘</td>          <td>&amp;weierp;</td>          <td>&amp;#8472;</td>          <td align="center" bgcolor="#f1f9ff">ℑ</td>          <td>&amp;image;</td>          <td>&amp;#8465;</td>        </tr>        <tr>          <td align="center" bgcolor="#f1f9ff">ℜ</td>          <td>&amp;real;</td>          <td>&amp;#8476;</td>          <td align="center" bgcolor="#f1f9ff">™</td>          <td>&amp;trade;</td>          <